<div style="width:0px; height:0px; overflow:hidden;">
<script type="text/javascript">
// -1 unknown, 0 no, 1 yes
var fbsc_loggedIn = -1;
var fbsc_permsOk = -1;
var fbsc_userName = '';

function fbsc_update_status() {

	var $j = jQuery.noConflict();

	$j("#fbsc_login_status_unknown").hide();
	$j("#fbsc_login_status_yes").hide();
	$j("#fbsc_login_status_no").hide();
	$j("#fbsc_publish_status_unknown").hide();
	$j("#fbsc_publish_status_yes").hide();
	$j("#fbsc_publish_status_no").hide();
	switch (fbsc_loggedIn) {
	case -1:
		$j("#fbsc_login_status_unknown").show();
		break;
	case 0:
		$j("#fbsc_login_status_no").show();
		break;
	case 1:
		$j("#fbsc_login_name").html(fbsc_userName);
		$j("#fbsc_login_status_yes").show();
		break;
	}
	switch (fbsc_permsOk) {
	case -1:
		$j("#fbsc_publish_status_unknown").show();
		break;
	case 0:
		$j("#fbsc_publish_status_no").show();
		break;
	case 1:
		$j("#fbsc_publish_status_yes").show();
		break;
	}
	if (fbsc_loggedIn == 1 && fbsc_permsOk == 1) {
		$j('.fbsc_form').show();
	} else {
		$j('.fbsc_forn').hide();
	}

}

function fbsc_do_login() {
	FB.Connect.requireSession(fbsc_check_login_status, true);
}

function fbsc_do_perms() {
	FB.Connect.showPermissionDialog('publish_stream', fbsc_check_publish_status);
}

function fbsc_check_login_status() {
	FB.Connect.ifUserConnected(fbsc_login_status_yes, fbsc_login_status_no);
}

function fbsc_login_status_yes() {
	fbsc_loggedIn = 1;
	fbsc_update_status();
	fbsc_check_username();
	fbsc_check_publish_status();
}

function fbsc_login_status_no() {
	fbsc_loggedIn = 0;
	fbsc_permsOk = -1;
	fbsc_userName = '';
	fbsc_update_status();
}

function fbsc_check_username() {
	var userid = FB.Connect.get_loggedInUser();
	FB.Facebook.apiClient.users_getInfo([userid], ['name'], fbsc_update_username);
}

function fbsc_update_username(ret) {
	if (ret !== null) {
		fbsc_userName = ret[0]['name'];
	} else {
		fbsc_userName = '';
	}
	fbsc_update_status();
}

function fbsc_check_publish_status() {
	FB.Facebook.apiClient.users_hasAppPermission('publish_stream', fbsc_check_publish_status_results);
}

function fbsc_check_publish_status_results(stat) {
	fbsc_permsOk = stat;
	fbsc_update_status();
}

FB_RequireFeatures(["Connect"], function(){ 
	FB.Facebook.init("<?php echo get_option('fbsc_api_key'); ?>", "<?php echo site_url(); ?>?fbsc=xd");
});
FB.ensureInit(fbsc_check_login_status);
</script>
